const intialData={
    list :[]
}

const TodoReducer =(state=intialData,action)=>{
switch(action.type){
    case "AddToDo":
        const {id,data}=action.payload;
        return{
            ...state,
            data:[
                ...state.list,
                {
                 id:id,
                 data:data
                }
            ]
        }
        default:return state;
}
}

export default TodoReducer;
