const initialData = {
  list: []
};

const TodoReducer = (state = initialData, action) => {
  switch (action.type) {
    case "AddToDo":
      const { id, data } = action.payload;
      return {
        ...state,
        list: [
          ...state.list,
          {
            id: id,
            data: data
          }
        ]
      };

    case "DeleteToDo":
      const newList = state.list.filter((elem) => elem.id !== action.id);
      return {
        ...state,
        list: newList
      };

    case "RemoveAll":
      return {
        ...state,
        list: []
      };

    default:
      return state;
  }
};

export default TodoReducer;
